mapper.select:
SELECT * FORM mainpage_article5
FULL JOIN(
    SELECT * FROM mainpage_article4 
    FULL JOIN(
        SELECT * FROM mainpage_article3 
        FULL JOIN(
            SELECT * FROM mainpage_article1 
            FULL JOIN mainpage_article2 USING(article_id)
        ) USING(article_id)
    )USING(article_id)
)USING(article_id);
